mapper.select:
SELECT gc.`id`,gc.`picturepath`,gc.`goods_title`,gt.`type_name`,gc.`goods_code`,gc.`createdate`,gc.`del` FROM `goods_commodity` gc,`goods_type` gt
and gc.`goods_name`like concat('%',#{goodsName},'%')
and gc.`ccategoryid`=#{ccategoryid}
and gc.ccategoryid=gt.id and gc.del!=1
order by createdate desc limit #{start},#{pageSize}

select gc.`id`,gc.`upper_down`,gc.`picturepath`,gc.`goods_name`,gt.`type_name`,gc.`goods_code`,gc.`createdate`,gc.`price`,gc.`weight`,gc.`del`,gc.details from `goods_commodity` gc,`goods_type` gt
and gc.ccategoryid=#{ccategoryid}
and gc.ccategoryid=gt.id and gc.upper_down=0 and gc.del=0

SELECT gc.`id`,gc.`upper_down`,gc.`picturepath`,gc.`bigpicturepath`,gc.`goods_name`,gt.`type_name`,gc.`goods_code`,gc.`createdate`,gc.`price`,gc.`weight`,gc.`del`,gc.details
 FROM `goods_commodity` gc,`goods_type` gt
 WHERE gt.id=gc.ccategoryid AND gc.bounds!=0 AND gc.upper_down=0 AND gc.del=0;

select gc.`id`,gc.`upper_down`,gc.`picturepath`,gc.`bigpicturepath`,gc.`goods_name`,gt.`type_name`,gc.`goods_code`,gc.`createdate`,gc.`price`,gc.`weight`,gc.`del`,gc.details
 from `goods_commodity` gc,`goods_type` gt
 where gt.id=gc.ccategoryid  and gc.presell!=0 and gc.upper_down=0 and gc.del=0;

select gc.`id`,gc.`upper_down`,gc.`picturepath`,gc.`bigpicturepath`,gc.`goods_name`,gt.`type_name`,gc.`goods_code`,gc.`createdate`,gc.`price`,gc.`weight`,gc.`del`,gc.details
 from `goods_commodity` gc,`goods_type` gt
 where gt.id=gc.ccategoryid and gc.activityid!=0 and gc.upper_down=0 and gc.del=0;
